mapper.select:
SELECT CARID,NAME,PLATE_NUM,TYPE,STATUS,ORIGIN
	FROM CAR



		  	and NAME=#{name}



		  	and PLATE_NUM=#{plate_num}



		  	and TYPE=#{type}



		  	and STATUS=#{status}



		  	and ORIGIN=#{origin}


	LIMIT #{startRow},#{pageSize}

SELECT COUNT(*) FROM CAR



	  	and NAME=#{name}



	  	and PLATE_NUM=#{plate_num}



	  	and TYPE=#{type}



	  	and STATUS=#{status}



	  	and ORIGIN=#{origin}

SELECT CARID,NAME,PLATE_NUM,TYPE,STATUS,ORIGIN
	FROM CAR
	WHERE CARID=#{carid}

mapper.update:
UPDATE CAR SET
	STATUS = 'N'
	WHERE CARID=#{carid}

UPDATE CAR SET
	STATUS = 'Y'
	WHERE CARID=#{carid}

mapper.select:
SELECT CARID,NAME,PLATE_NUM,TYPE,STATUS,ORIGIN
	FROM CAR
	WHERE PLATE_NUM = #{plate_num}

SELECT CARID,NAME,PLATE_NUM,TYPE,STATUS,ORIGIN
	FROM CAR



		  	and NAME=#{name}



		  	and PLATE_NUM=#{plate_num}



		  	and TYPE=#{type}



		  	and STATUS=#{status}



		  	and ORIGIN=#{origin}


	LIMIT #{startRow},#{pageSize}

SELECT COUNT(*) FROM CAR



	  	and NAME=#{name}



	  	and PLATE_NUM=#{plate_num}



	  	and TYPE=#{type}



	  	and STATUS=#{status}



	  	and ORIGIN=#{origin}
